import { Component, OnInit } from '@angular/core';
import { UserService } from '../../services/user.service';
import { RoleService } from '../../services/role.service';
import { User, Role } from '../../models/user.model';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent implements OnInit {
  users: User[] = [];
  roles: Role[] = [];
  loading = false;
  error = '';

  constructor(
    private userService: UserService,
    private roleService: RoleService
  ) {}

  ngOnInit(): void {
    this.loadUsers();
    this.loadRoles();
  }

  loadUsers(): void {
    this.loading = true;
    this.userService.getUsers().subscribe({
      next: (users) => {
        this.users = users;
        this.loading = false;
        console.log('Users loaded:', users);
      },
      error: (error) => {
        this.error = 'Failed to load users: ' + (error.error?.message || error.message);
        this.loading = false;
        console.error('Error loading users:', error);
      }
    });
  }

  loadRoles(): void {
    this.roleService.getRoles().subscribe({
      next: (roles) => {
        this.roles = roles;
        console.log('Roles loaded:', roles);
      },
      error: (error) => {
        this.error = 'Failed to load roles: ' + (error.error?.message || error.message);
        console.error('Error loading roles:', error);
      }
    });
  }

  onRoleChange(event: Event, userId: string): void {
    const newRole = (event.target as HTMLSelectElement).value;
    this.updateUserRole(userId, newRole);
  }

  updateUserRole(userId: string, newRole: string): void {
    this.userService.updateUserRole(userId, { role: newRole }).subscribe({
      next: () => {
        this.loadUsers(); // Reload users to get updated data
      },
      error: (error) => {
        this.error = 'Failed to update user role: ' + (error.error?.message || error.message);
        console.error('Error updating user role:', error);
      }
    });
  }

  deleteUser(userId: string): void {
    if (confirm('Are you sure you want to delete this user?')) {
      this.userService.deleteUser(userId).subscribe({
        next: () => {
          this.loadUsers(); // Reload users after deletion
        },
        error: (error) => {
          this.error = 'Failed to delete user: ' + (error.error?.message || error.message);
          console.error('Error deleting user:', error);
        }
      });
    }
  }

  getRoleBadgeClass(roleName: string): string {
    switch (roleName) {
      case 'Admin': return 'badge-admin';
      case 'Editor': return 'badge-editor';
      case 'Viewer': return 'badge-viewer';
      default: return 'badge-secondary';
    }
  }
}
<div class="row">
  <div class="col-12">
    <div class="card">
      <div class="card-header d-flex justify-content-between align-items-center">
        <h4>User Management</h4>
        <a routerLink="/register" class="btn btn-primary">Add New User</a>
      </div>
      <div class="card-body">
        <div class="alert alert-danger" *ngIf="error">
          {{ error }}
        </div>

        <!-- Debug info -->
        <div class="alert alert-info" *ngIf="roles.length === 0">
          Loading roles... ({{ roles.length }} roles loaded)
        </div>

        <div class="text-center" *ngIf="loading">
          <div class="spinner-border" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
        </div>

        <div class="table-responsive" *ngIf="!loading">
          <table class="table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Status</th>
                <th>Last Login</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let user of users">
                <td>{{ user.fullName }}</td>
                <td>{{ user.email }}</td>
                <td>
                  <select 
                    class="form-select form-select-sm" 
                    [value]="user.role.name"
                    (change)="onRoleChange($event, user._id)"
                    [disabled]="roles.length === 0"
                  >
                    <option *ngIf="roles.length === 0" value="">Loading roles...</option>
                    <option *ngFor="let role of roles" [value]="role.name">
                      {{ role.name }}
                    </option>
                  </select>
                </td>
                <td>
                  <span class="badge" [ngClass]="getRoleBadgeClass(user.role.name)">
                    {{ user.role.name }}
                  </span>
                </td>
                <td>
                  <span *ngIf="user.isActive" class="badge bg-success">Active</span>
                  <span *ngIf="!user.isActive" class="badge bg-danger">Inactive</span>
                </td>
                <td>
                  {{ user.lastLogin ? (user.lastLogin | date:'short') : 'Never' }}
                </td>
                <td>
                  <button 
                    class="btn btn-danger btn-sm"
                    (click)="deleteUser(user._id)"
                    [disabled]="user.role.name === 'Admin'">
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        <div class="text-center" *ngIf="!loading && users.length === 0">
          <p class="text-muted">No users found.</p>
        </div>
      </div>
    </div>
  </div>
</div>
